import React, { useRef, useState } from 'react';

const ProjectPage = () => {
  const carouselRef = useRef(null);

  const scrollAmount = 400; // how far to scroll on click (px)

  const handleNext = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: scrollAmount, behavior: 'smooth' });
    }
  };

  const handlePrev = () => {
    if (carouselRef.current) {
      carouselRef.current.scrollBy({ left: -scrollAmount, behavior: 'smooth' });
    }
  };

  return (
    <div className="h-screen flex flex-col overflow-hidden overflow-y-auto">
      {/* Header and SideNavBar remain the same */}
      <div
        className="flex-grow bg-cover bg-center flex flex-col items-center px-4"
        style={{ backgroundImage: "url('/images/background.svg')" }}
      >
        <h1 className="text-4xl font-bold text-indigo-600 mt-12 mb-12 text-center">
          My Projects
        </h1>

        <div className="flex items-center gap-4">
          {/* Prev Button */}
          <button
            onClick={handlePrev}
            className="w-12 h-12 flex items-center justify-center rounded-full bg-indigo-600 hover:bg-indigo-800 transition duration-300"
            aria-label="Previous"
          >
            <img src="/images/arrow_left.svg" alt="Arrow" className="w-10 h-10" />
          </button>

          {/* Scrollable Carousel */}
          <div
            ref={carouselRef}
            className="flex overflow-x-auto scroll-smooth gap-8 max-w-[116rem] pl-8 pr-8"
          >
            {projects.map((project, index) => (
              <div
                key={index}
                className="min-w-[20rem] max-w-[24rem] bg-gray-100 rounded-2xl shadow-md hover:shadow-xl hover:scale-[1.02] transition duration-300 p-4 flex-shrink-0"
              >
                <h2 className="text-xl font-semibold text-indigo-600 mb-2">{project.title}</h2>
                <p className="text-gray-700 mb-4">{project.description}</p>
                <div className="overflow-hidden rounded-md">
                  <img
                    src={project.staticRef}
                    alt={project.alt}
                    className="rounded-md w-auto h-[16.5rem]"
                  />
                </div>
                {project.link ? (
                  <a
                    href={project.link}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-indigo-600 hover:underline font-medium mt-4 block"
                  >
                    Project Repository â†’
                  </a>
                ) : (
                  <span className="text-indigo-600 hover:text-gray-500 font-medium mt-4 block">
                    Private Repository
                  </span>
                )}
              </div>
            ))}
          </div>

          {/* Next Button */}
          <button
            onClick={handleNext}
            className="w-12 h-12 flex items-center justify-center rounded-full bg-indigo-600 hover:bg-indigo-800 transition duration-300"
            aria-label="Next"
          >
            <img src="/images/arrow_right.svg" alt="Arrow" className="w-10 h-10" />
          </button>
        </div>
      </div>
    </div>
  );
};
