import React from 'react';
import { useNavigate } from 'react-router-dom';

const SideNavBar = () => {
  const navigate = useNavigate();

  return (
    <div className="fixed top-1/2 left-0 transform -translate-y-1/2 group">
      <div className="relative bg-indigo-600 text-white rounded-r-lg w-10 h-24 flex items-center justify-center transition-all duration-300 group-hover:w-32 group-hover:h-80">
        <div className="flex flex-col items-center w-full">
          {/* Arrow Icon */}
          <div className="mb-2 transform transition-transform duration-00 group-hover:hidden">
            <img
              src="/images/arrow.svg"
              alt="hover"
              className="w-8 h-8 object-cover"
            />
          </div>

          {/* Logos: use opacity + translateY with delay */}
          <div className="absolute top-0 left-0 w-full h-full flex-col items-start pl-6 pt-8 pb-8 text-sm opacity-0 scale-95 translate-y-4 pointer-events-none transition-all duration-300 delay-300 ease-in group-hover:opacity-100 group-hover:scale-100 group-hover:translate-y-0 group-hover:pointer-events-auto flex">
            <img
              src="/images/linkedin.svg"
              alt="Profile"
              className="w-16 h-16 object-cover mb-8 transition-transform duration-300 hover:scale-[1.2]"
              onClick={() => navigate("/about")}
            />
            <img
              src="/images/indeed.svg"
              alt="Profile"
              className="w-16 h-16 object-cover mb-8 transition-transform duration-300 hover:scale-[1.2]"
              onClick={() => navigate("/about")}
            />
            <img
              src="/images/github.svg"
              alt="Profile"
              className="w-16 h-16 object-cover transition-transform duration-300 hover:scale-[1.2]"
              onClick={() => navigate("/about")}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default SideNavBar;
