import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import SideNavBar from '../components/SideNavBar';
import { useTranslation } from "react-i18next";

const projects = [
  {
    title: "IRIS",
    description: "An insurance policy review system developed for BFL Canada. Allowing users to compare, annotate and review policy documents.",
    gifRef: "/gifs/iris.gif",
    staticRef: "/images/iris.png",
    alt: "Iris",
    link: "",
  },
  {
    title: "Cancer Pathology Report Annotation",
    gifRef: "/gifs/annotation.gif",
    staticRef: "/images/annotation.png",
    alt: "Cancer Annotation",
    description: "Annotating Breast Cancer Pathology Reports from BC Cancer data to faciliate the training of an NLP model.",
    link: "https://github.com/Acr0batOG/BC_Cancer_Honours_Project",
  },
  {
    title: "On-Body Scrolling in VR",
    gifRef: "/gifs/onbody.gif",
    staticRef: "/images/on-body.png",
    alt: "On-Body Scrolling",
    description: "A Unity-based VR application that allows users to scroll through content using on-body gestures.",
    link: "https://github.com/Acr0batOG/VRScrollingHand",
  },
  {
    title: "eduQuest",
    description: "An interactive e-learning platform designed to make education more engaging.",
    gifRef: "/gifs/eduquest.gif",
    staticRef: "/images/eduquest.png",
    alt: "eduQuest",
    link: "https://github.com/chpsmstr/eduQuest",
  },
];

const ProjectPage = () => {
  const [hoveredProject, setHoveredProject] = useState(null);
  const [startIndex, setStartIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const [slideDirection, setSlideDirection] = useState(null);
  const carouselRef = useRef(null);

  const canGoNext = startIndex + 3 < projects.length;
  const canGoPrev = startIndex > 0;

  const handleNext = () => {
    if (canGoNext && !isTransitioning) {
      setIsTransitioning(true);
      setSlideDirection('right');
      
      setTimeout(() => {
        setStartIndex(startIndex + 1);
        setTimeout(() => {
          setIsTransitioning(false);
        }, 50); // Small delay to prevent rapid clicking
      }, 500); // Match this with transition duration
    }
  };

  const handlePrev = () => {
    if (canGoPrev && !isTransitioning) {
      setIsTransitioning(true);
      setSlideDirection('left');
      
      setTimeout(() => {
        setStartIndex(startIndex - 1);
        setTimeout(() => {
          setIsTransitioning(false);
        }, 50); // Small delay to prevent rapid clicking
      }, 500); // Match this with transition duration
    }
  };

  // Calculate which projects to show
  const visibleProjects = projects.slice(startIndex, startIndex + 3);
  
  // For animation, we need to include previous/next items that will slide in
  const prevProject = startIndex > 0 ? projects[startIndex - 1] : null;
  const nextProject = startIndex + 3 < projects.length ? projects[startIndex + 3] : null;

  return (
    <div className="h-screen flex flex-col overflow-hidden overflow-y-auto">
      <Header />
      <SideNavBar />
      <div
        className="flex-grow bg-cover bg-center flex flex-col items-center px-4"
        style={{ backgroundImage: "url('/images/background.svg')" }}
      >
        <h1 className="text-4xl font-bold text-indigo-600 mt-12 mb-12 text-center">
          My Projects
        </h1>

        <div className="flex items-center gap-4 w-full max-w-6xl justify-center">
          <button
            onClick={handlePrev}
            disabled={!canGoPrev || isTransitioning}
            className={`w-12 h-12 flex items-center justify-center rounded-xl 
              ${canGoPrev && !isTransitioning ? 'bg-indigo-600 hover:bg-indigo-800 opacity-97 transition duration-300' : 'bg-gray-400'}`}
            aria-label="Previous"
          >
            <img
              src="/images/arrow_left.svg"
              alt="Arrow"
              className="w-10 h-10"
            />
          </button>

          <div 
            ref={carouselRef}
            className="overflow-hidden relative flex-1" 
            style={{ maxWidth: 'calc(100% - 6rem)' }}
          >
            <div 
              className={`flex transition-transform duration-500 ease-in-out`}
              style={{ 
                transform: isTransitioning && slideDirection === 'right' 
                  ? 'translateX(-33.33%)' 
                  : isTransitioning && slideDirection === 'left'
                  ? 'translateX(33.33%)'
                  : 'translateX(0)',
                width: '100%'
              }}
            >
              {/* Grid container for all visible projects */}
              <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8 w-full">
                {visibleProjects.map((project, index) => (
                  <div
                    key={startIndex + index}
                    className="bg-gray-100 rounded-2xl shadow-md hover:shadow-lg hover:scale-[1.02] transition duration-300 pl-6 pr-6 pb-4 pt-4"
                    onMouseEnter={() => setHoveredProject(index)}
                    onMouseLeave={() => setHoveredProject(null)}
                  >
                    <h2 className="text-xl font-semibold text-indigo-600 mb-2">
                      {project.title}
                    </h2>
                    <p className="text-gray-700 mb-4">{project.description}</p>
                    <div className="overflow-hidden rounded-md">
                      <img
                        src={hoveredProject === index ? project.gifRef : project.staticRef}
                        alt={project.alt}
                        className="rounded-md w-full h-64 object-cover"
                      />
                    </div>
                    {project.link ? (
                      <a
                        href={project.link}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-indigo-600 hover:underline font-medium mt-4 block"
                      >
                        Project Repository →
                      </a>
                    ) : (
                      <span className="text-indigo-600 hover:text-gray-500 font-medium mt-4 block">
                        Private Repository
                      </span>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>

          <button
            onClick={handleNext}
            disabled={!canGoNext || isTransitioning}
            className={`w-12 h-12 flex items-center justify-center rounded-xl  
              ${canGoNext && !isTransitioning ? 'bg-indigo-600 opacity-97 hover:bg-indigo-800 transition' : 'bg-gray-400'}`}
            aria-label="Next"
          >
            <img
              src="/images/arrow_right.svg"
              alt="Arrow"
              className="w-10 h-10"
            />
          </button>
        </div>
        
        {/* Pagination indicators */}
        <div className="flex gap-2 mt-8 mb-8">
          {Array.from({ length: Math.ceil(projects.length / 3) }).map((_, index) => (
            <div 
              key={index}
              className={`w-3 h-3 rounded-full ${Math.floor(startIndex / 3) === index ? 'bg-indigo-600' : 'bg-gray-300'}`}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProjectPage;
