import React from 'react';


const SideNavBar = () => {
  return (
    <div className="fixed top-1/2 left-0 transform -translate-y-1/2 group">
      <div className="bg-indigo-600 text-white rounded-r-lg w-10 h-24 flex items-center justify-center transition-all duration-300 group-hover:w-40 group-hover:h-28">
        <div className="flex flex-col items-center w-full">
          {/* Arrow Icon */}
          <div className="mb-2 transform transition-transform group-hover:rotate-180">
           <img
           src="/images/arrow.svg"
           alt="Profile"
           className="w-full h-full object-cover"
           onClick={() => navigate("/about")}
        />
          </div> 

          {/* Hidden content that shows on hover */}
          <div className="hidden group-hover:flex flex-col items-start px-2 space-y-2 text-sm w-full">
    
              <FaGithub className="inline mr-2" /> GitHub
            
            
              <FaLinkedin className="inline mr-2" /> LinkedIn
            
          </div>
        </div>
      </div>
    </div>
  );
};

export default SideNavBar;
