import React from 'react';
import NavBar from '../components/NavBar';
import Footer from '../components/Footer';
import { newURL } from '../libraries/file-path-provider';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from "react-i18next";
import { getSessionRole } from '../auth/auth';
import { logout } from '../auth/logout';
import { useState, useEffect } from 'react';

const HomePage = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  let jwt = localStorage.getItem('jwt');
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    async function validate() {
      const token = await localStorage.getItem('jwt');
      const role = await getSessionRole(token); // false if invalid token, "broker"/"manager"/"admin" if valid
      //console.log("role in NavBar.js: " + role);
      if (role === false) {
        setIsLoggedIn(false);
        return;
      } else {
        setIsLoggedIn(true);
      }
    }
    validate();
  }, []);
  const handleClick = () => {
    if (isLoggedIn) {
      logout();
      window.location.reload();
    } else {
      navigate('/login');
    }
  };

  return (
    <>
      <div
        className="min-h-screen bg-cover bg-center flex flex-col relative"
        style={{
          backgroundImage: `url(${newURL('/images/background3.webp')})`,
        }}
      >
        <NavBar />
        <div className="flex-grow flex flex-col">
          <div className="flex flex-col mt-16 pl-2 lg:pl-64">
            <h1 className="tracking-tight font-medium text-[1.5rem] lg:text-[2.5rem] text-white mb-0">
              {t("homep1")}{(' ')} <span className="text-[#ABD2FF]" aria-label="iris_title">IRIS</span>.
            </h1>
            <div className="w-max">
              <h2 className="animate-typing overflow-hidden whitespace-nowrap border-r-4 border-r-white pr-5 tracking-tight font-normal text-[1.2rem] lg:text-[2rem] text-white mb-0">
                {t("homep2")}
              </h2>
            </div>
            <p className="tracking-tight font-light text-[1rem] lg:text-[1.3rem] text-white mb-0 max-w-[45rem]">
              {t("homep3")}
            </p>
            <div className="flex mt-12 justify-normal mb-auto">
              <button
                className="bg-blue-700 rounded px-24 py-2 hover:bg-blue-600 transition-colors duration-500 hover:scale-[101%] text-xl text-white font-medium"
                onClick={handleClick}
                aria-label="login_button"
              >
                {isLoggedIn ? t("logout") : t("login")}
              </button>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default HomePage;
