import React from 'react';
import Header from '../components/Header';
import SideNavBar from '../components/SideNavBar';

const AboutPage = () => {
  return (
    <div className="h-screen flex flex-col overflow-hidden overflow-y-auto">
      <Header />
      <SideNavBar />
      <div
        className="flex-grow bg-cover bg-center flex flex-col items-center px-4"
        style={{
          backgroundImage: "url('/images/background.svg')",
        }}
      >
        <h1 className="text-4xl font-bold text-indigo-600 mt-24 mb-10 text-center">
          About Me
        </h1>
        <div className="bg-gray-100 p-8 rounded-2xl shadow-md w-full max-w-3xl text-gray-800">
          <p className="mb-6">
            I’m a full-stack developer passionate about design, coding, and everything in between. I enjoy building interactive web applications and engaging games that bring ideas to life through thoughtful design and clean, efficient code.
          </p>
          <p className="mb-6">
            My journey in software development blends creativity with logic—whether it’s crafting intuitive user interfaces or building scalable backends. I'm always eager to learn new technologies, explore design trends, and collaborate on meaningful projects.
          </p>
          <p>
            Outside of coding, I enjoy experimenting with visual design, learning new tools, and staying up-to-date with the latest in tech and user experience.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutPage;
