import React from 'react';
import { useNavigate }  from 'react-router-dom';


    


const SideNavBar = () => {
    const navigate = useNavigate();
  return (
    <div className="fixed top-1/2 left-0 transform -translate-y-1/2 group">
      <div className="bg-indigo-600 text-white rounded-r-lg w-10 h-24 flex items-center justify-center transition-all duration-250 group-hover:w-32 group-hover:h-80">
        <div className="flex flex-col items-center w-full">
          {/* Arrow Icon */}
          <div className="mb-2 transform transition-transform group-hover:hidden">
           <img
           src="/images/arrow.svg"
           alt="hover"
           className="w-8 h-8 object-cover"
        />
          </div> 

                    {/* Hidden content that shows on hover */}
        <div className="hidden transition-all duration-500 group-hover:flex flex-col items-start pl-6 pt-8 pb-8 text-sm w-full">
            <img
                src="/images/linkedin.svg"
                alt="Profile"
                className="w-16 h-16 object-cover mb-8 transition-all duration-500  hover:scale-[1.2]" 
                onClick={() => navigate("/about")}
            />
            <img
                src="/images/indeed.svg"
                alt="Profile"
                className="w-16 h-16 object-cover mb-8 transition-all duration-500  hover:scale-[1.2]"  
                onClick={() => navigate("/about")}
            />
            <img
                src="/images/github.svg"
                alt="Profile"
                className="w-16 h-16 object-cover transition-all duration-500  hover:scale-[1.2]" 
                onClick={() => navigate("/about")}
            />
            </div>
            

        </div>
      </div>
    </div>
  );
};

export default SideNavBar;
