import React from 'react';

const projects = [
  {
    title: "Portfolio Website",
    description: "A personal portfolio built with React and Tailwind CSS.",
    link: "https://your-portfolio.com",
  },
  {
    title: "Weather App",
    description: "A weather forecasting app using OpenWeatherMap API.",
    link: "https://weather-app.com",
  },
  {
    title: "Memory Card Game",
    description: "A React-based memory game using hooks and state management.",
    link: "https://memory-game.com",
  },
  // Add more projects here
];

const ProjectPage = () => {
  return (
    <div className="h-screen flex flex-col overflow-hidden overflow-y-auto">
      <Header />
      <SideNavBar />
      <div
        className="flex-grow bg-cover bg-center flex flex-col items-center px-4"
        style={{
          backgroundImage: "url('/images/background.svg')",
        }}
      >
      <h1 className="text-3xl font-bold text-indigo-600 mb-8 text-center">
        My Projects
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {projects.map((project, index) => (
          <div
            key={index}
            className="bg-gray-100 rounded-2xl shadow-md hover:shadow-lg transition duration-300 p-6"
          >
            <h2 className="text-xl font-semibold text-indigo-600 mb-2">
              {project.title}
            </h2>
            <p className="text-gray-700 mb-4">{project.description}</p>
            <a
              href={project.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-indigo-600 hover:underline font-medium"
            >
              View Project â†’
            </a>
          </div>
        ))}
      </div>
    </div>
    </div>
  );
};

export default ProjectPage;
