import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';

const HomePage = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 to-indigo-800 flex items-center justify-center px-4">
      <Header/>
      <div className="max-w-4xl text-white space-y-8 text-center">
        <h1 className="text-4xl lg:text-6xl font-bold tracking-tight">
          Hi, I'm <span className="text-yellow-300">Rhys Smith</span>
        </h1>

        <h2 className="text-xl lg:text-2xl font-medium text-blue-100">
          A Developer, Designer, and Creator of Things.
        </h2>

        <p className="text-base lg:text-lg text-blue-200 leading-relaxed">
          Welcome to my personal site where I share my projects, ideas, and passion for technology. Explore what I've built and how I can help bring your ideas to life.
        </p>

        <div className="flex justify-center gap-6 mt-6 flex-wrap">
          <button
            onClick={() => navigate("/projects")}
            className="bg-yellow-400 hover:bg-yellow-300 text-blue-900 font-semibold px-6 py-2 rounded-lg shadow-md transition-transform transform hover:scale-105"
          >
            View Projects
          </button>
          <button
            onClick={() => navigate("/contact")}
            className="bg-white hover:bg-gray-100 text-blue-800 font-semibold px-6 py-2 rounded-lg shadow-md transition-transform transform hover:scale-105"
          >
            Contact Me
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
