import React, { useState } from "react";
import { FaLinkedin, FaGithub } from "react-icons/fa";
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";

const SideNavBar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNav = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div
      className={`fixed top-0 left-0 h-full ${isOpen ? "w-64" : "w-20"} bg-indigo-800 transition-all duration-300`}
    >
      {/* Arrow Icon */}
      <div
        className={`absolute top-1/2 left-0 transform -translate-y-1/2 ${isOpen ? "rotate-180" : ""} transition-all duration-300`}
        onClick={toggleNav}
        style={{ padding: "10px", cursor: "pointer" }}
      >
        <AiOutlineArrowLeft size={30} color="#fff" />
      </div>

      {/* Links */}
      <div className="flex flex-col items-center mt-12 space-y-8">
        <a href="https://www.linkedin.com" target="_blank" rel="noopener noreferrer">
          <FaLinkedin
            size={isOpen ? 40 : 30}
            color="#fff"
            className="transition-all duration-300"
          />
        </a>
        <a href="https://www.github.com" target="_blank" rel="noopener noreferrer">
          <FaGithub
            size={isOpen ? 40 : 30}
            color="#fff"
            className="transition-all duration-300"
          />
        </a>
      </div>
    </div>
  );
};

export default SideNavBar;
